    public void SetUserPermission(String uRole) {
            if ("teacher".equals(uRole.toLowerCase())) {
                jMI_FileCreateUser.setEnabled(false);
                jMI_ProfileTeaAdd.setEnabled(false);
                jMI_ProfileEmpAdd.setEnabled(false);
                jMI_LibraryAddBook.setEnabled(false);
                jMI_LibraryBorrowBook.setEnabled(false);
                jMI_AccountsStuFees.setEnabled(false);
                jMI_AccountsSalaryPayment.setEnabled(false);
                jMI_AccountsAccessoryBills.setEnabled(false);
            } else if ("employee".equals(uRole.toLowerCase())) {
                jMI_FileCreateUser.setEnabled(false);
                jMI_ProfileStuAdd.setEnabled(false);
                jMI_ProfileTeaAdd.setEnabled(false);
                jMI_ProfileEmpAdd.setEnabled(false);
                jMI_ResultCreate.setEnabled(false);
                jMI_ResultSearch.setEnabled(false);
                jMI_LibraryAddBook.setEnabled(false);
                jMI_LibraryBorrowBook.setEnabled(false);
                jMI_LibraryReport.setEnabled(false);
            } else if ("librarian".equals(uRole.toLowerCase())) {
                jMI_FileCreateUser.setEnabled(false);
                jMI_ProfileStuAdd.setEnabled(false);
                jMI_ProfileTeaAdd.setEnabled(false);
                jMI_ProfileTeaSearch.setEnabled(false);
                jMI_ProfileEmpAdd.setEnabled(false);
                jMI_ProfileEmpSearch.setEnabled(false);
                jMI_ResultCreate.setEnabled(false);
                jMI_ResultSearch.setEnabled(false);
                jMI_AccountsStuFees.setEnabled(false);
                jMI_AccountsSalaryPayment.setEnabled(false);
                jMI_AccountsAccessoryBills.setEnabled(false);
            } else {
                JOptionPane.showMessageDialog(null, "WELCOME TO THE SYSTEM - Sir, Admin");
            }
            jL_userRole.setText(uRole.toUpperCase());
            this.setVisible(true);
    }
    
